package com.example.calculadoraimc.ui.dashboard

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.fragment.app.Fragment
import com.example.calculadoraimc.databinding.FragmentDashboardBinding
import com.chaquo.python.Python

class DashboardFragment : Fragment() {

    private var _binding: FragmentDashboardBinding? = null
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentDashboardBinding.inflate(inflater, container, false)
        val root: View = binding.root

        setupClickListeners()

        return root
    }

    private fun setupClickListeners() {
        binding.btnCalcular.setOnClickListener {
            calcularPercentil()
        }

        binding.btnGuardar.setOnClickListener {
            guardarMedicion()
        }
    }

    private fun calcularPercentil() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()
        val edadText = binding.etEdad.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty() || edadText.isEmpty()) {
            Toast.makeText(context, "Por favor completa todos los campos", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()
            val edad = edadText.toInt()

            if (peso <= 0 || altura <= 0 || edad < 5 || edad > 19) {
                Toast.makeText(context, "Valores inválidos. Edad debe estar entre 5-19 años", Toast.LENGTH_SHORT).show()
                return
            }

            val sexo = if (binding.rbMasculino.isChecked) "Masculino" else "Femenino"
            val edadMeses = edad * 12 // Convertir años a meses

            // Llamar a las funciones de Python
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            val resultado = funcionesModule.callAttr("calcular_imc_menor", sexo, edadMeses, imc)

            // Verificar si hay error en el resultado
            if (resultado.containsKey("error")) {
                Toast.makeText(context, "Error: ${resultado["error"]}", Toast.LENGTH_LONG).show()
                return
            }

            val percentil = resultado.get("percentil").toDouble()
            val interpretacion = resultado.get("interpretacion").toString()

            // Mostrar resultados
            binding.tvImcValor.text = "IMC: ${String.format("%.1f", imc)}"
            binding.tvPercentil.text = "Percentil: ${String.format("%.1f", percentil)}"
            binding.tvInterpretacion.text = interpretacion
            binding.cardResultado.visibility = View.VISIBLE

        } catch (e: NumberFormatException) {
            Toast.makeText(context, "Por favor ingresa valores numéricos válidos", Toast.LENGTH_SHORT).show()
        } catch (e: Exception) {
            Toast.makeText(context, "Error al calcular: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun guardarMedicion() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()
        val edadText = binding.etEdad.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty() || edadText.isEmpty()) {
            Toast.makeText(context, "Calcula el percentil primero", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()
            val edad = edadText.toInt()
            val sexo = if (binding.rbMasculino.isChecked) "Masculino" else "Femenino"
            val edadMeses = edad * 12

            // Llamar a las funciones de Python
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            val resultado = funcionesModule.callAttr("calcular_imc_menor", sexo, edadMeses, imc)

            if (!resultado.containsKey("error")) {
                val percentil = resultado.get("percentil").toDouble()
                funcionesModule.callAttr("guardar_medicion", peso, altura, imc, sexo, edadMeses, percentil)

                Toast.makeText(context, "Medición guardada en el historial", Toast.LENGTH_SHORT).show()

                // Limpiar campos
                binding.etPeso.text?.clear()
                binding.etAltura.text?.clear()
                binding.etEdad.text?.clear()
                binding.rbMasculino.isChecked = true
                binding.cardResultado.visibility = View.GONE
            }

        } catch (e: Exception) {
            Toast.makeText(context, "Error al guardar: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}

