    }

    private fun calcularPercentil() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()
        val edadText = binding.etEdad.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty() || edadText.isEmpty()) {
            Toast.makeText(context, "Por favor completa todos los campos", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()
            val edad = edadText.toInt()

            if (peso <= 0 || altura <= 0 || edad < 5 || edad > 19) {
                Toast.makeText(context, "Valores inválidos. Edad debe estar entre 5-19 años", Toast.LENGTH_SHORT).show()
                return
            }

            val sexo = if (binding.rbMasculino.isChecked) "Masculino" else "Femenino"
            val edadMeses = edad * 12 // Convertir años a meses

            // Llamar a las funciones de Python
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            val resultado = funcionesModule.callAttr("calcular_imc_menor", sexo, edadMeses, imc)

            // Verificar si hay error en el resultado
            if (resultado?.containsKey("error") == true) {
                Toast.makeText(context, "Error: ${resultado["error"]}", Toast.LENGTH_LONG).show()
                return
            }

            val percentil = resultado?.get("percentil")?.toDouble() ?: 0.0
            val interpretacion = resultado?.get("interpretacion")?.toString() ?: "Sin interpretación"

            // Mostrar resultados
            binding.tvImcValor.text = "IMC: ${String.format("%.1f", imc)}"
            binding.tvPercentil.text = "Percentil: ${String.format("%.1f", percentil)}"
            binding.tvInterpretacion.text = interpretacion
            binding.cardResultado.visibility = View.VISIBLE

        } catch (e: NumberFormatException) {
            Toast.makeText(context, "Por favor ingresa valores numéricos válidos", Toast.LENGTH_SHORT).show()
        } catch (e: Exception) {
            Toast.makeText(context, "Error al calcular: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun guardarMedicion() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()
        val edadText = binding.etEdad.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty() || edadText.isEmpty()) {
            Toast.makeText(context, "Calcula el percentil primero", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()
            val edad = edadText.toInt()
            val sexo = if (binding.rbMasculino.isChecked) "Masculino" else "Femenino"
            val edadMeses = edad * 12

            // Llamar a las funciones de Python
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            val resultado = funcionesModule.callAttr("calcular_imc_menor", sexo, edadMeses, imc)

            if (!resultado.containsKey("error")) {
                val percentil = resultado.get("percentil").toDouble()
                funcionesModule.callAttr("guardar_medicion", peso, altura, imc, sexo, edadMeses, percentil)

                Toast.makeText(context, "Medición guardada en el historial", Toast.LENGTH_SHORT).show()

                // Limpiar campos
                binding.etPeso.text?.clear()
                binding.etAltura.text?.clear()
                binding.etEdad.text?.clear()
                binding.rbMasculino.isChecked = true
                binding.cardResultado.visibility = View.GONE
            }

        } catch (e: Exception) {
            Toast.makeText(context, "Error al guardar: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}

package com.example.calculadoraimc.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import com.example.calculadoraimc.databinding.FragmentHomeBinding

class HomeFragment : Fragment() {

    private var _binding: FragmentHomeBinding? = null

    // This property is only valid between onCreateView and
    // onDestroyView.
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        val homeViewModel =
            ViewModelProvider(this).get(HomeViewModel::class.java)

        _binding = FragmentHomeBinding.inflate(inflater, container, false)
        val root: View = binding.root

        val textView: TextView = binding.textHome
        homeViewModel.text.observe(viewLifecycleOwner) {
            textView.text = it
        }
        return root
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}

package com.example.calculadoraimc.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Toast
import androidx.fragment.app.Fragment
import com.example.calculadoraimc.databinding.FragmentHomeBinding
import com.chaquo.python.Python

class HomeFragment : Fragment() {

    private var _binding: FragmentHomeBinding? = null
    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        _binding = FragmentHomeBinding.inflate(inflater, container, false)
        val root: View = binding.root

        setupClickListeners()

        return root
    }

    private fun setupClickListeners() {
        binding.btnCalcular.setOnClickListener {
            calcularIMC()
        }

        binding.btnGuardar.setOnClickListener {
            guardarMedicion()
        }
    }

    private fun calcularIMC() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty()) {
            Toast.makeText(context, "Por favor ingresa peso y altura", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()

            if (peso <= 0 || altura <= 0) {
                Toast.makeText(context, "Los valores deben ser mayores a 0", Toast.LENGTH_SHORT).show()
                return
            }

            // Llamar a la función de Python para calcular IMC
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            val interpretacion = funcionesModule.callAttr("interpretar_imc", imc).toString()

            // Mostrar resultados
            binding.tvImcValor.text = "IMC: ${String.format("%.1f", imc)}"
            binding.tvInterpretacion.text = interpretacion
            binding.cardResultado.visibility = View.VISIBLE

        } catch (e: NumberFormatException) {
            Toast.makeText(context, "Por favor ingresa valores numéricos válidos", Toast.LENGTH_SHORT).show()
        } catch (e: Exception) {
            Toast.makeText(context, "Error al calcular: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    private fun guardarMedicion() {
        val pesoText = binding.etPeso.text.toString()
        val alturaText = binding.etAltura.text.toString()

        if (pesoText.isEmpty() || alturaText.isEmpty()) {
            Toast.makeText(context, "Calcula el IMC primero", Toast.LENGTH_SHORT).show()
            return
        }

        try {
            val peso = pesoText.toDouble()
            val altura = alturaText.toDouble()

            // Llamar a las funciones de Python
            val python = Python.getInstance()
            val funcionesModule = python.getModule("funciones_imc_android")

            val imc = funcionesModule.callAttr("calcular_imc", peso, altura).toDouble()
            funcionesModule.callAttr("guardar_medicion", peso, altura, imc)

            Toast.makeText(context, "Medición guardada en el historial", Toast.LENGTH_SHORT).show()

            // Limpiar campos
            binding.etPeso.text?.clear()
            binding.etAltura.text?.clear()
            binding.cardResultado.visibility = View.GONE

        } catch (e: Exception) {
            Toast.makeText(context, "Error al guardar: ${e.message}", Toast.LENGTH_SHORT).show()
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}

package com.example.calculadoraimc.ui.views

import android.content.Context
import android.graphics.*
import android.util.AttributeSet
import android.view.View
import androidx.core.content.ContextCompat
import com.example.calculadoraimc.R

class BarraIMC @JvmOverloads constructor(
    context: Context,
    attrs: AttributeSet? = null,
    defStyleAttr: Int = 0
) : View(context, attrs, defStyleAttr) {

    private val paint = Paint(Paint.ANTI_ALIAS_FLAG)
    private val textPaint = Paint(Paint.ANTI_ALIAS_FLAG)
    private val indicatorPaint = Paint(Paint.ANTI_ALIAS_FLAG)

    private var currentIMC: Float = 0f
    private var showIndicator = false

    // Colores para cada rango de IMC
    private val colorBajoPeso = Color.parseColor("#2196F3")      // Azul
    private val colorNormal = Color.parseColor("#4CAF50")        // Verde
    private val colorSobrepeso = Color.parseColor("#FF9800")     // Naranja
    private val colorObesidad1 = Color.parseColor("#FF5722")     // Rojo naranja
    private val colorObesidad2 = Color.parseColor("#D32F2F")     // Rojo
    private val colorObesidad3 = Color.parseColor("#7B1FA2")     // Morado

    // Rangos de IMC
    private val ranges = listOf(
        IMCRange("Bajo peso", "<18.5", 0f, 18.5f, colorBajoPeso),
        IMCRange("Normal", "18.5-24.9", 18.5f, 24.9f, colorNormal),
        IMCRange("Sobrepeso", "25-29.9", 25f, 29.9f, colorSobrepeso),
        IMCRange("Obesidad I", "30-34.9", 30f, 34.9f, colorObesidad1),
        IMCRange("Obesidad II", "35-39.9", 35f, 39.9f, colorObesidad2),
        IMCRange("Obesidad III", "≥40", 40f, 50f, colorObesidad3)
    )

    init {
        textPaint.apply {
            textSize = 28f
            color = Color.BLACK
            textAlign = Paint.Align.CENTER
        }

        indicatorPaint.apply {
            color = Color.BLACK
            strokeWidth = 6f
            style = Paint.Style.FILL
        }
    }

    override fun onDraw(canvas: Canvas?) {
        super.onDraw(canvas)
        canvas?.let { drawBarraIMC(it) }
    }

    private fun drawBarraIMC(canvas: Canvas) {
        val barHeight = height * 0.3f
        val barTop = height * 0.35f
        val barBottom = barTop + barHeight

        val totalWidth = width.toFloat()
        val segmentWidth = totalWidth / ranges.size

        // Dibujar segmentos de la barra
        ranges.forEachIndexed { index, range ->
            val left = index * segmentWidth
            val right = left + segmentWidth

            paint.color = range.color
            canvas.drawRect(left, barTop, right, barBottom, paint)

            // Dibujar texto del rango
            val centerX = left + segmentWidth / 2

            // Nombre del rango
            canvas.drawText(range.name, centerX, barTop - 20f, textPaint)

            // Valores del rango
            textPaint.textSize = 24f
            canvas.drawText(range.displayRange, centerX, barBottom + 40f, textPaint)
            textPaint.textSize = 28f
        }

        // Dibujar indicador si hay un IMC establecido
        if (showIndicator && currentIMC > 0) {
            drawIndicator(canvas, barTop, barBottom)
        }
    }

    private fun drawIndicator(canvas: Canvas, barTop: Float, barBottom: Float) {
        val position = calculatePosition(currentIMC)
        val x = position * width

        // Dibujar línea indicadora
        indicatorPaint.style = Paint.Style.STROKE
        indicatorPaint.strokeWidth = 6f
        canvas.drawLine(x, barTop - 40f, x, barBottom + 60f, indicatorPaint)

        // Dibujar triángulo en la parte superior
        val trianglePath = Path().apply {
            moveTo(x, barTop - 10f)
            lineTo(x - 15f, barTop - 40f)
            lineTo(x + 15f, barTop - 40f)
            close()
        }
        indicatorPaint.style = Paint.Style.FILL
        canvas.drawPath(trianglePath, indicatorPaint)

        // Dibujar valor del IMC
        val imcText = String.format("%.1f", currentIMC)
        textPaint.color = Color.BLACK
        textPaint.textSize = 32f
        textPaint.typeface = Typeface.DEFAULT_BOLD
        canvas.drawText(imcText, x, barTop - 50f, textPaint)
        textPaint.typeface = Typeface.DEFAULT
        textPaint.textSize = 28f
    }

    private fun calculatePosition(imc: Float): Float {
        return when {
            imc < 18.5f -> (imc / 18.5f) * (1f / ranges.size)
            imc < 25f -> (1f / ranges.size) + ((imc - 18.5f) / (24.9f - 18.5f)) * (1f / ranges.size)
            imc < 30f -> (2f / ranges.size) + ((imc - 25f) / (29.9f - 25f)) * (1f / ranges.size)
            imc < 35f -> (3f / ranges.size) + ((imc - 30f) / (34.9f - 30f)) * (1f / ranges.size)
            imc < 40f -> (4f / ranges.size) + ((imc - 35f) / (39.9f - 35f)) * (1f / ranges.size)
            else -> (5f / ranges.size) + ((imc - 40f) / 10f) * (1f / ranges.size)
        }
    }

    fun setIMC(imc: Float) {
        currentIMC = imc
        showIndicator = true
        invalidate()
    }

    fun clearIndicator() {
        showIndicator = false
        invalidate()
    }

    override fun onMeasure(widthMeasureSpec: Int, heightMeasureSpec: Int) {
        val desiredHeight = 200
        val height = resolveSize(desiredHeight, heightMeasureSpec)
        super.onMeasure(widthMeasureSpec, MeasureSpec.makeMeasureSpec(height, MeasureSpec.EXACTLY))
    }

    data class IMCRange(
        val name: String,
        val displayRange: String,
        val minValue: Float,
        val maxValue: Float,
        val color: Int
    )
}
